import React from "react";
import ADD_TODO from "../actions/action";

const initialstate = [];

const Todoreducer = (state = initialstate, action) => {
  switch (action.type) {
    case ADD_TODO:
      return (state = [...state,{
        id : Math.random()
      }]);

    default:
      return state;
  }
};
export default Todoreducer;
