const ADD_TODO = "ADD_TODO";
export default ADD_TODO
export const DEL_TODO = 'DEL_TODO'
export const addtodo = text =>({
    type : ADD_TODO ,
    id : Math.random(),
    text
})
export const deletetodo = id =>({
    type : DEL_TODO ,
    id : id

})
